const menuBtn=document.querySelector(".heder__nav-btn"),mobileMenu=document.querySelector(".header__nav-list--mobile"),passivSvg=document.querySelector(".heder__btn-svg"),activeSvg=document.querySelector(".heder__btn-svg--active");let menuOpen=!1;menuBtn.addEventListener("click",e=>{menuOpen?(menuOpen=!1,mobileMenu.classList.add("none"),passivSvg.classList.remove("none"),activeSvg.classList.add("none")):(menuOpen=!0,mobileMenu.classList.remove("none"),passivSvg.classList.add("none"),activeSvg.classList.remove("none"))});